(6,	1,	'2020-04-21 17:59:43',	'2020-04-21 17:59:43',	'<!-- wp:heading {\"align\":\"center\",\"className\":\"sub-title\"} -->\n<h2 class=\"has-text-align-center sub-title\">La plongée à Belle-Île-en-Mer</h2>\n<!-- /wp:heading -->\n\n<!-- wp:buttons {\"align\":\"center\",\"className\":\"cta_home\"} -->\n<div class=\"wp-block-buttons aligncenter cta_home\"><!-- wp:button {\"className\":\"is-style-fill\"} -->\n<div class=\"wp-block-button is-style-fill\"><a class=\"wp-block-button__link\" href=\"https://www.angelus-plongee.com/bapteme/\">Offrez un baptême !</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:buttons -->\n\n<!-- wp:html -->\n<section id=\"home\">\n    <div class=\"container\">\n        <div class=\"row home-content\">\n            <div class=\"col-sm\">\n                <div class=\"testimony text-center text-white\">\n                    <h3>Découvrez l\'île et ses couleurs.</h3>\n                    <h3>A 45 mn du continent, <br>venez plonger en Bretagne et découvrir les merveilles de Belle-Île-en-Mer.</h3>\n                </div>\n                <div class=\"img-center logo-home\" data-aos=\"fade-up\" data-aos-easing=\"linear\" data-aos-duration=\"500\">\n                    <figure class=\"text-center size-large is-resized\"><img src=\"https://www.angelus-plongee.com/wp-content/themes/angelus-plongee-child/assets/img/Logo-Angelus-Plongeex250.png\" alt=\"\" class=\"\" width=\"220\" height=\"220\"></figure>\n                </div>\n                <div class=\"container-number\">\n                    <div class=\"number\"><span class=\"count-up\">30010</span> Plongées</div>\n                    <div class=\"number\"><span>10</span> Spots</div>\n                    <div class=\"number\"><span class=\"count-up\">4100</span> Baptêmes</div>\n                </div>\n            </div>\n        </div>\n        <div class=\"row mt-2\">\n            <div class=\"col\">\n                <div class=\"arrow-container\"><a href=\"#about\" class=\"smooth-scroll arrow-down\"><svg class=\"bi bi-arrow-down-short\" width=\"65px\" height=\"65px\" preserveAspectRatio=\"xMidYMid slice\" stroke=\"white\" stroke-width=\"1\" viewBox=\"0 0 20 20\" fill=\"white\" xmlns=\"http://www.w3.org/2000/svg\">\n  <path fill-rule=\"evenodd\" d=\"M6.646 9.646a.5.5 0 01.708 0L10 12.293l2.646-2.647a.5.5 0 01.708.708l-3 3a.5.5 0 01-.708 0l-3-3a.5.5 0 010-.708z\" clip-rule=\"evenodd\"></path>\n  <path fill-rule=\"evenodd\" d=\"M10 6.5a.5.5 0 01.5.5v5a.5.5 0 01-1 0V7a.5.5 0 01.5-.5z\" clip-rule=\"evenodd\"></path>\n</svg></a>\n                    <div class=\"line-arrow\"></div>\n                </div>\n            </div>\n        </div>\n    </div>\n</section>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<section id=\"about\">\n    <div class=\"container\">\n        <div class=\"row mb-4 about-content text-center\">\n            <div class=\"col-sm\">\n                <h2 class=\"text-center pt-3\">Qui sommes-nous</h2>\n                <h3 class=\"text-center pt-1 pl-1 pr-1\"><a href=\"/qui-sommes-nous/\">Angélus Plongée</a> est implantée<br class=\"rwd-break\"> à Belle-Ile-en-Mer <br class=\"rwd-break\">depuis 20 ans.</h3>\n                <p class=\"text-center pt-3 pl-4 pr-4\">Ses moniteurs de plongée, tous professionnels, diplômés d’État, vous feront partager leur expérience et leur connaissance des sites.</p>\n                <h6 class=\"text-center pt-3 pb-2 pl-4 pr-4\">We speak english.</h6>\n                <div class=\"about-team\">\n                    <button data-href=\"lequipe\" type=\"button\" class=\"openBtn btn btn-primary\" data-toggle=\"modal\" data-target=\"#lequipe\">L\'équipe</button>\n                    <br><br>\n                    <button data-href=\"le-bateau\" type=\"button\" class=\"openBtn btn btn-primary\" data-toggle=\"modal\" data-target=\"#le-bateau\">Le bateau</button>\n                </div>\n            </div>\n        </div>\n        <div class=\"row\">\n            <div class=\"col-sm\">\n                <div class=\"arrow-container\"><a href=\"#activity\" class=\"smooth-scroll arrow-down\"><svg class=\"bi bi-arrow-down-short\" width=\"65px\" height=\"65px\" preserveAspectRatio=\"xMidYMid slice\" stroke=\"white\" stroke-width=\"1\" viewBox=\"0 0 20 20\" fill=\"white\" xmlns=\"http://www.w3.org/2000/svg\">\n                            <path fill-rule=\"evenodd\" d=\"M6.646 9.646a.5.5 0 01.708 0L10 12.293l2.646-2.647a.5.5 0 01.708.708l-3 3a.5.5 0 01-.708 0l-3-3a.5.5 0 010-.708z\" clip-rule=\"evenodd\"></path>\n                            <path fill-rule=\"evenodd\" d=\"M10 6.5a.5.5 0 01.5.5v5a.5.5 0 01-1 0V7a.5.5 0 01.5-.5z\" clip-rule=\"evenodd\"></path>\n                        </svg></a>\n                    <div class=\"line-arrow\"></div>\n                </div>\n            </div>\n        </div>\n    </div>\n</section>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<section id=\"activity\">\n    <div class=\"container\">\n        <div class=\"row activity-content\">\n            <div class=\"col-sm\">\n                <h2 class=\"text-center pt-5\">Activités &amp; Tarifs</h2>\n                <ul class=\"activity-fare\">\n                    <li class=\"activity-fare-item\"><a href=\"/bapteme\">Baptême</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/stage-plongee-decouverte\">Stage plongée découverte</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/randonnee-a-la-palme\">Randonnée à la palme</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/exploration\">Exploration</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/remise-a-niveau\">Remise à niveau</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/formation\">Formation</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/plongee-enfants\">Plongée enfants</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/centres-de-vacances\">Centres de vacances</a></li>\n                    <li class=\"activity-fare-item\"><a href=\"/groupes-auto-encadres/\">Groupes auto-encadrés</a></li>\n                </ul>\n<div class=\"wp-block-buttons aligncenter cta_home\">\n<div class=\"wp-block-button is-style-fill\"><a class=\"wp-block-button__link\" href=\"https://www.angelus-plongee.com/bretagne/activites_tarifs//\">Réservez maintenant</a></div></div>\n            </div>\n        </div>\n        <div class=\"row\">\n            <div class=\"col-sm\">\n                <div class=\"arrow-container\"><a href=\"#testimony\" class=\"smooth-scroll arrow-down\"><svg class=\"bi bi-arrow-down-short\" width=\"65px\" height=\"65px\" preserveAspectRatio=\"xMidYMid slice\" stroke=\"white\" stroke-width=\"1\" viewBox=\"0 0 20 20\" fill=\"white\" xmlns=\"http://www.w3.org/2000/svg\">\n                            <path fill-rule=\"evenodd\" d=\"M6.646 9.646a.5.5 0 01.708 0L10 12.293l2.646-2.647a.5.5 0 01.708.708l-3 3a.5.5 0 01-.708 0l-3-3a.5.5 0 010-.708z\" clip-rule=\"evenodd\"></path>\n                            <path fill-rule=\"evenodd\" d=\"M10 6.5a.5.5 0 01.5.5v5a.5.5 0 01-1 0V7a.5.5 0 01.5-.5z\" clip-rule=\"evenodd\"></path>\n                        </svg></a>\n                    <div class=\"line-arrow\"></div>\n                </div>\n            </div>\n        </div>\n    </div>\n</section>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<section id=\"testimony\">\n    <div class=\"container\">\n        <div class=\"row testimonials\">\n            <div class=\"col\">\n                <div class=\"slick-carousel owl-theme owl-drag aos-init aos-animate\" data-aos=\"zoom-in\">\n                    <div>\n                        <h4>Ludovic</h4>\n                        <h5>Une expérience inoubliable</h5>\n                        <p> Avec Jordie et David, impossible de ne pas profiter pleinement de la plongée. Que ça soit randonnée PMT, plongée à Deuborh ou à la Basse de Palais, ils savent mettre en confiance et vous apprennent à aimer la plongée.<br>\n                            Encore merci pour cette saison les gars. C’était vraiment trop top !\n                            Rendez-vous l’année prochaine.</p>\n                    </div>\n                    <div>\n                        <h4>Cédric</h4>\n                        <h5>Plongées sympas !</h5>\n                        <p>Accueil et plongées sympathiques en compagnie de David et Jordie. <br>\n                            Les sites : La basse de palais et l’épave du Philippe Eric, situés au large du port de Palais. Du matériel récent et de qualité. <br>\n                            Bonne ambiance à bord et un banc de dauphins passant au large pour clore la sortie. <br>\n                            A découvrir !</p>\n                    </div>\n                    <div>\n                        <h4>Sarah</h4>\n                        <h5>A conseiller sans hésiter</h5>\n                        <p>David et Jordie font fonctionner cette structure avec beaucoup de passion pour la mer et pour la plongée sous-marine, et ça se ressent... <br> présentation des plongées, ambiance à bord du bateau, encadrement des plongées... bref que du bon...</p>\n                    </div>\n                    <div>\n                        <h4>Céline</h4>\n                        <h5>Le top !</h5>\n                        <p>Je souhaitais faire découvrir la plongée à mon fils de 9 ans et me remettre à l’eau après 10 ans sans plonger. <br>Mon fils a été conquis, j’ai été enthousiasmée par la beauté et la richesse des fonds de la plage de Deuborh ainsi que par la qualité du matériel et de l’encadrement. Au cours de mon séjour à Belle Ile, j’ai effectué 5 autres plongées...</p>\n                    </div>\n                    <div>\n                        <h4>Maxime et Fabienne</h4>\n                        <h5>Absolument parfait</h5>\n                        <p>Nous n’avions prévu que 2 plongées pendant ces vacances à Belle Ile... sans grande conviction... <br>\n                            Grosse erreur : l’endroit mérite plus et Angélus plongée fait vraiment tout pour que les sorties soient un vrai plaisir\n                            J’ai découvert, dans les 20 m, des fonds superbement colorés (tapissés d’anémones bijoux), et pleins de vie...</p>\n                    </div>\n                    <div>\n                        <h4>Hélène</h4>\n                        <h5>Découverte des plongées Bretonnes</h5>\n                        <p>Arrivés de Lyon... tout était prêt à notre arrivée... Une grande première avec 5 plongées en Bretagne... <br> Des sites choisis avec soins par David, des explications claires, beaucoup de confort, une grande disponibilité, organisation à la carte, des plongeurs bien chouchoutés... Que du bonheur parole de plongeuse Niveau 3 !</p>\n                    </div>\n                </div>\n            </div>\n        </div>\n        <div class=\"row\">\n            <div class=\"col text-center\">\n                <h6><a href=\"temoignages\">Voir tous les témoignages</a></h6>\n            </div>\n        </div>\n        <div class=\"row\">\n            <div class=\"col\">\n                <div class=\"arrow-container\"><a href=\"#belleile\" class=\"smooth-scroll arrow-down\"><svg class=\"bi bi-arrow-down-short\" width=\"65px\" height=\"65px\" preserveAspectRatio=\"xMidYMid slice\" stroke=\"white\" stroke-width=\"1\" viewBox=\"0 0 20 20\" fill=\"white\" xmlns=\"http://www.w3.org/2000/svg\">\n                            <path fill-rule=\"evenodd\" d=\"M6.646 9.646a.5.5 0 01.708 0L10 12.293l2.646-2.647a.5.5 0 01.708.708l-3 3a.5.5 0 01-.708 0l-3-3a.5.5 0 010-.708z\" clip-rule=\"evenodd\"></path>\n                            <path fill-rule=\"evenodd\" d=\"M10 6.5a.5.5 0 01.5.5v5a.5.5 0 01-1 0V7a.5.5 0 01.5-.5z\" clip-rule=\"evenodd\"></path>\n                        </svg></a>\n                    <div class=\"line-arrow\"></div>\n                </div>\n            </div>\n        </div>\n    </div>\n</section>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<section id=\"belleile\">\n    <div class=\"container\">\n        <div class=\"row mb-5 contact-content\">\n            <div class=\"col\">\n                <h2 class=\"text-center pt-3 pl-2 pr-2\">Belle-Île-en-Mer</h2>\n                <h3 class=\"text-white text-center pl-1 pr-1\">Un havre, un paradis, <span class=\"rwd-break\"><br></span>un refuge</h3>\n                <div id=\"roundbel\">\n                    <div class=\"roundpict1\"></div>\n                    <div class=\"roundpict2\"></div>\n                    <div class=\"roundpict3\"></div>\n                    <div class=\"content-spots\">\n                        <div class=\"text-spots\">SPOTS</div>\n                    </div>\n                </div>\n            </div>\n        </div>\n        <div class=\"row\">\n            <div class=\"col\">\n                <div class=\"arrow-right-belleile\">\n                    <a href=\"belle-ile\" class=\"arrow-right\">Découvrir / Dormir<span><svg aria-hidden=\"true\" focusable=\"false\" data-prefix=\"fas\" data-icon=\"arrow-circle-right\" class=\"svg-inline--fa fa-arrow-circle-right fa-w-16\" role=\"img\" xmlns=\"http://www.w3.org/2000/svg\" width=\"50\" height=\"50\" viewBox=\"0 0 512 512\">\n                                <path fill=\"currentColor\" d=\"M256 8c137 0 248 111 248 248S393 504 256 504 8 393 8 256 119 8 256 8zm-28.9 143.6l75.5 72.4H120c-13.3 0-24 10.7-24 24v16c0 13.3 10.7 24 24 24h182.6l-75.5 72.4c-9.7 9.3-9.9 24.8-.4 34.3l11 10.9c9.4 9.4 24.6 9.4 33.9 0L404.3 273c9.4-9.4 9.4-24.6 0-33.9L271.6 106.3c-9.4-9.4-24.6-9.4-33.9 0l-11 10.9c-9.5 9.6-9.3 25.1.4 34.4z\"></path>\n                            </svg></span></a>\n                </div>\n            </div>\n        </div>\n    </div>\n</section>\n<!-- /wp:html -->\n\n<!-- wp:html -->\n<div id=\"backtop\"><a href=\"#angel\" class=\"top\"><svg class=\"bi bi-arrow-up\" width=\"2em\" height=\"2em\" preserveAspectRatio=\"xMidYMid slice\" stroke=\"#B4C712\" stroke-width=\"2\" viewBox=\"0 0 20 20\" fill=\"currentColor\" xmlns=\"http://www.w3.org/2000/svg\">\n	<title>Haut</title>\n  <path fill-rule=\"evenodd\" d=\"M10 5.5a.5.5 0 01.5.5v9a.5.5 0 01-1 0V6a.5.5 0 01.5-.5z\" clip-rule=\"evenodd\"></path>\n  <path fill-rule=\"evenodd\" d=\"M9.646 4.646a.5.5 0 01.708 0l3 3a.5.5 0 01-.708.708L10 5.707 7.354 8.354a.5.5 0 11-.708-.708l3-3z\" clip-rule=\"evenodd\"></path>\n</svg></a></div>\n<!-- /wp:html -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->',	'Angelus Plongée',	'',	'publish',	'closed',	'closed',	'',	'angelus-plongee',	'',	'',	'2020-05-04 23:48:03',	'2020-05-04 23:48:03',	'',	0,	'https://www.angelus-plongee.com/?page_id=6',	0,	'page',	'',	0),
(20,	1,	'2020-04-22 09:41:00',	'2020-04-22 09:41:00',	'<!-- wp:html -->\n<ul class=\"nav nav-tabs\" id=\"bellenav\" role=\"tablist\">\n    <li class=\"nav-item\">\n        <a class=\"nav-link active\" id=\"decouvrir-tab\" data-toggle=\"tab\" href=\"#decouvrir\" role=\"tab\" aria-controls=\"decouvrir\" aria-selected=\"true\">Découvrir</a>\n    </li>\n    <li class=\"nav-item\">\n        <a class=\"nav-link\" id=\"info-tab\" data-toggle=\"tab\" href=\"#info\" role=\"tab\" aria-controls=\"info\" aria-selected=\"false\">Infos &amp; Accès</a>\n    </li>\n    <li class=\"nav-item\">\n        <a class=\"nav-link\" id=\"loger-tab\" data-toggle=\"tab\" href=\"#loger\" role=\"tab\" aria-controls=\"loger\" aria-selected=\"false\">Se loger</a>\n    </li>\n</ul>\n<div class=\"tab-content mt-3 mb-3\" id=\"belleile-enmer\">\n    <div class=\"tab-pane fade show active\" id=\"decouvrir\" role=\"tabpanel\" aria-labelledby=\"decouvrir-tab\">\n        <h2>Découvrir</h2>\n        <h3>Un havre, un paradis, un refuge</h3>\n        <figure><img src=\"https://www.angelus-plongee.com/relook/wp-content/uploads/2020/04/crique-belle-ile-en-mer_x320.jpg\" alt=\"\" width=\"100%\" height=\"auto\"></figure>\n        <p><strong>Angélus Plongée vous guide sur ses sentiers sous-marins.</strong><br>\n            Belle-Île-En-Mer (20 km de long sur 9 de large) s’élève à 71 mètres au-dessus de la mer. Véritable palette de couleurs et de contrastes, Belle Île en Mer est à chaque instant différente. La diversité de ses paysages en fait son charme : landes, falaises abruptes, criques d’eau verte mais aussi bois, vallons, ruisseaux, champs et longues plages de sable.</p><br>\n        <p>Au voyageur qui arrive du continent, elle présente, de la pointe des Poulains à Kerdonis, sa côte bien abritée des vents dominants, avec ses vallons verdoyants, ses criques et ses plages, ses coteaux fleuris et de petits villages ombragés par leurs bouquets d’arbres.</p>\n        <p>Une impression de calme et de fraîcheur se dégage de ce versant est de l’île où s’abrite le port fleuri de Sauzon et celui de Le Palais, chef-lieu et port d’arrivée, que la citadelle Vauban semble encore vouloir protéger.</p><br>\n        <p>Battue par les vents du large, à l’ouest, la côte sauvage est d’une beauté violente et sans cesse renouvelée ; falaises déchiquetées, rochers aux formes étranges, grottes profondes…</p><br>\n        <p>A pied, à vélo, en voiture, en avion, en bateau et même en kayak de mer, vous pourrez découvrir Belle Île en Mer.</p>\n    </div>\n    <div class=\"tab-pane fade\" id=\"info\" role=\"tabpanel\" aria-labelledby=\"info-tab\">\n        <h2>Infos &amp; Accès</h2>\n        <p><a href=\"http://www.belle-ile.com/\" target=\"_blank\" rel=\"noopener noreferrer\">L’Office de Tourisme</a> de Belle-Île vous accueille toute l’année à Le Palais.</p>\n        <p>Pour rejoindre Belle Île toute l’année\n            Liaisons Quiberon – Le Palais\n            <a href=\"http://www.compagnie-oceane.fr/fr/belleile/ile_f.php\" target=\"_blank\" rel=\"noopener noreferrer\">La Compagnie Océane</a> assure le service toute l’année pour les passagers et les véhicules (entre 5 à 13 allers-retours par jour suivant la saison)</p>\n        <p><strong>Durée de la traversée :</strong> 55 minutes, arrivée au port de Le Palais ou 35 minutes (vedette rapide, l’été uniquement, pas de véhicules)</p>\n        <p><strong>Pour rejoindre Quiberon par la route</strong><br>\n            De Paris <br>\n            Emprunter l’A11 jusqu’au Mans, puis l’A81 jusqu’à Rennes. Se diriger en suite vers Vannes par la RN 24 jusqu’à Ploermel, puis la RN 166.</p>\n        <p>Depuis Nantes <br>\n            Suivre la direction de Vannes.</p>\n        <p>De Vannes <br>\n            Emprunter la RN 165 (direction Lorient) jusqu’à Auray, puis suivre la direction de Quiberon (28 km).</p>\n        <p><strong>Du 1er avril au 2 octobre :</strong> stationnement obligatoire en parkings payants et gardés avec navette gratuite pour la gare maritime</p>\n        <p>Parking de KERNE (300 places) 24/24h : <a href=\"tel:+330686626918\">06.86.62.69.18</a> <br><a href=\"https://www.parkingdekerne.com/Bienvenue.html\" target=\"_blank\" rel=\"noopener noreferrer\">www.parkingdekerne.com</a> <br> <a href=\"mailto:info@parkingdekerne.com\">info@parkingdekerne.com</a><br></p>\n        <p>Le Sémaphore (1300 places) : ouvert du 1er avril au 3 octobre : <a href=\"tel:+33297305945\">02 97 30 59 45</a></p>\n        <p>Á proximité de la gare maritime\n            <strong>Parking de Port Maria</strong><br>\n            (80 places) – <a href=\"tel:+33297500758\">02 97 50 07 58</a> <br></p>\n        <p>Le Parking de l’Océan se trouve derrière l’Hôtel de l’Océan au 7 Quai de l’Océan, face au port de Port-Maria.\n            Il est situé, à 250m de la gare maritime et des embarcadères pour Belle-Île et dispose de plus de 80 places.\n            <a href=\"#\" target=\"_blank\" rel=\"noopener noreferrer\">Quiberon parking</a></p>\n    </div>\n    <div class=\"tab-pane fade\" id=\"loger\" role=\"tabpanel\" aria-labelledby=\"loger-tab\">\n        <h2>Se loger</h2>\n        <h3>Le Camping de l’Océan</h3>\n<figure><img src=\"https://www.angelus-plongee.com/relook/wp-content/uploads/2020/04/camping-oceanx320.jpg\" alt=\"\" width=\"100%\" height=\"auto\"></figure>\n        <p>Notre partenaire, situé à deux pas du centre de Le Palais, du débarcadère et des départs de plongée, vous propose des hébergements meublés : des chalets, des mobil homes, des tentes aménagées, ou des roulottes.\n            Vous serez à proximité du centre de Le Palais (15 à 20 mn de marche), des commerces, des restaurants, et des autres animations.</p><br>\n        <p>Le camping, équipé d’une piscine chauffée ouverte de mai à septembre, à la capacité d’accueillir vos groupes de plongeurs et vos familles car il dispose d’un grand nombre de locatifs différents (chalets ou mobil homes) d’une capacité de 2 à 6 personnes. Ils sont tous équipés pour cuisiner, sont composés de 2 ou 3 chambres, et possèdent tous une terrasse avec salon de jardin.</p><br>\n        <p>De plus, ils ont aménagés dans leur camping un <strong>« Espace Plongeurs »</strong> auprès des sanitaires où vous pourrez nettoyer vos combinaisons et matériel à l’eau claire au retour de vos plongées. </p><br>\n        <p>Son <strong>Restaurant Snack</strong>, ouvert hors saison sur demande pour les groupes, pourra vous proposer des repas et des petits déjeuners si vous le souhaitez.</p><br>\n        <p>Consultez leur site internet (lien ci dessous) et contactez les directement par mail ou par téléphone, afin de leur demander un devis.</p>\n        <p>Ils se feront un plaisir de vous répondre dans les meilleurs délais.</p><br>\n        <p><a href=\"https://www.camping-ocean-belle-ile.com\" target=\"_blank\" rel=\"noopener noreferrer\">www.camping-ocean-belle-ile.com</a><br>\n            Tél : <a href=\"tel:+33297318386\">02 97 31 83 86</a> <br>\n            Email : <a href=\"mailto:info@camping-ocean-belle-ile.com\">info@camping-ocean-belle-ile.com</a></p>\n    </div>\n</div>\n<!-- /wp:html -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:heading -->\n<h2><a href=\"https://www.nytimes.com/interactive/2020/travel/places-to-visit.html?action=click&amp;module=Top%20Stories&amp;pgtype=Homepage#item-belle-ile\" target=\"_blank\" rel=\"noreferrer noopener\">New York Times</a></h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>The aptly named Belle-Île-en-Mer (“Beautiful Island in the Sea”), the largest island off the coast of Brittany, is on the cusp of a new wave of popularity. One of the greenest and most pristine destinations in France, it has recently seen the opening of excellent locavore restaurants, like the Japanese-born chef Haruka Casters’s <a rel=\"noreferrer noopener\" href=\"http://www.aobelleile.com/\" target=\"_blank\">Restaurant AO</a>, and stylishly renovated hotels, including the 1880-vintage <a rel=\"noreferrer noopener\" href=\"https://www.hotelduphare-belle-ile.fr/\" target=\"_blank\">Hotel du Phare</a>, whose refurbishment was led by the architect Jean-Michel Wilmotte. <br><br>A favored late-19th-century hideaway for artists like Claude Monet, who made its dramatic beaches the subject of many paintings, and the actress Sarah Bernhardt, who created a summer house from a disused stone fort at the Pointe des Poulains, Belle-Île is 45 minutes from Quiberon by ferry. An electric-rental-car service, inaugurated in 2019, is the latest in a variety of eco-responsible initiatives intended to preserve its natural beauty. (Others include a specialized team that maintains the island’s 56 miles of hiking trails and rebuilds the dunes along its magnificent and strictly protected coastline.)</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>—ALEXANDER LOBRANO</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->',	'Belle île en mer',	'',	'publish',	'closed',	'closed',	'',	'belle-ile-en-mer',	'',	'',	'2020-05-04 23:00:43',	'2020-05-04 23:00:43',	'',	0,	'https://www.angelus-plongee.com/?page_id=20',	0,	'page',	'',	0),
(22,	1,	'2020-04-22 09:46:39',	'2020-04-22 09:46:39',	'<!-- wp:heading -->\n<h2>Nous trouver à Belle-Île</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p><strong>En juillet et août</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>sur notre stand tous les matins de 9h30 à 12h30 sur le marché à Le Palais.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Nos départs explos</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Sur la Cale Bonnelle, près du phare rouge à l’entrée du port de Le Palais.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>Nos départs plages</strong></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Plage de Deuborh, entre Sauzon &amp; la Pointe des Poulains.</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Pour vous rendre à la plage de Deuborh,<br>entrez ces coordonnées dans la barre de recherche sur Google Maps : 47°22’45.5″N 3°14’16.2″W</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p><strong>ou cliquez sur ce lien :&nbsp;</strong><a href=\"https://www.google.fr/maps/place/Le+Fort+de+Deubord,+56360+Sauzon/@47.3791978,-3.2393456,17z/data=!4m7!1m4!3m3!1s0x481a82f609c652b9:0xd4764a8ee13a98ea!2sLe+Fort+de+Deubord,+56360+Sauzon!3b1!3m1!1s0x481a82f5d3994165:0x47e8a4b87df0db94\">Deuborh</a></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:html -->\n<hr>\n<!-- /wp:html -->\n\n<!-- wp:heading {\"level\":6} -->\n<h6 id=\"reservation\"><strong>Informations &amp; réservations</strong></h6>\n<!-- /wp:heading -->\n\n<!-- wp:list -->\n<ul><li>Pour vos réservations, merci de remplir le formulaire ci-dessous</li><li>Par téléphone :&nbsp;<a href=\"tel:+33685138376\">06 85 13 83 76</a></li><li><a href=\"mailto:info@angelus-plongee.com\">Par email</a></li></ul>\n<!-- /wp:list -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator\"/>\n<!-- /wp:separator -->\n\n<!-- wp:shortcode -->\n[formidable id=3]\n<!-- /wp:shortcode -->',	'Contact & Accès',	'',	'publish',	'closed',	'clos